mapper.select:
SELECT drbfm_single_testTask.*, bpm_inst.STATUS_ as status, bpm_inst.INST_ID_ as instId, bpm_inst.ACT_INST_ID_, act_ru_task.NAME_ AS currentProcessTask, act_ru_task.ID_ AS taskId, drbfm_total_struct.structName, drbfm_total_baseInfo.analyseName, drbfm_total_baseInfo.jixing, drbfm_total_struct.analyseUserName, drbfm_verifyAbilityList.abilityName FROM drbfm_single_testTask LEFT JOIN drbfm_verifyAbilityList ON drbfm_single_testTask.verifyAbilityId = drbfm_verifyAbilityList.id left join bpm_inst on drbfm_single_testTask.id=bpm_inst.BUS_KEY_ left join drbfm_single_baseinfo on drbfm_single_testTask.belongSingleId = drbfm_single_baseinfo.id LEFT JOIN drbfm_total_struct ON drbfm_single_baseinfo.structId = drbfm_total_struct.id LEFT JOIN drbfm_total_baseInfo ON drbfm_total_struct.belongTotalId = drbfm_total_baseinfo.id LEFT JOIN act_ru_task ON bpm_inst.ACT_INST_ID_ = act_ru_task.PROC_INST_ID_
1=1
AND drbfm_single_testTask.verifyAbilityId IN
#{item}
AND drbfm_single_testTask.testNumber like CONCAT('%',#{testNumber},'%')
AND drbfm_single_baseinfo.structType = #{structType}
AND drbfm_total_baseInfo.jixing like CONCAT('%',#{jixing},'%')
and drbfm_total_baseInfo.analyseName like CONCAT('%',#{analyseName},'%')
and drbfm_total_struct.structName like CONCAT('%',#{structName},'%')
and drbfm_total_struct.analyseUserName like CONCAT('%',#{analyseUserName},'%')
