mapper.select:
SELECT environmental_gsvehicle.cxId, environmental_gsvehicle.cpjxxh, environmental_gsvehicle.applyId, environmental_gsvehicle.applyName, environmental_gsvehicle.deptId, environmental_gsvehicle.deptName, environmental_gsvehicle.oldCxId, bpm_inst.STATUS_ AS STATUS from environmental_gsvehicle LEFT JOIN os_rel_inst ON environmental_gsvehicle.CREATE_BY_ = os_rel_inst.PARTY2_ LEFT JOIN os_group ON os_rel_inst.PARTY1_ = os_group.GROUP_ID_ LEFT JOIN os_user ON os_user.USER_ID_ = environmental_gsvehicle.CREATE_BY_ LEFT JOIN bpm_inst ON environmental_gsvehicle.cxId = bpm_inst.BUS_KEY_
os_rel_inst.REL_TYPE_KEY_ = 'GROUP-USER-BELONG' AND os_rel_inst.DIM1_ = '1'
AND environmental_gsvehicle.CREATE_TIME_ >= #{startTime}
AND environmental_gsvehicle.CREATE_TIME_ <= #{endTime}

SELECT count(*) as monthNum, finishTime as monthName FROM ( SELECT cxId, DATE_FORMAT( finishTime,'%Y-%m' ) as finishTime, createTime FROM ( SELECT bpm_inst.BUS_KEY_ AS cxId, bpm_node_jump.NODE_NAME_ AS nodeName, bpm_node_jump.COMPLETE_TIME_ AS finishTime, environmental_gsvehicle.CREATE_TIME_ as createTime FROM bpm_node_jump LEFT JOIN bpm_inst ON bpm_node_jump.ACT_INST_ID_ = bpm_inst.ACT_INST_ID_ left join environmental_gsvehicle on environmental_gsvehicle.cxId=bpm_inst.BUS_KEY_ WHERE bpm_inst.STATUS_ = 'SUCCESS_END' AND bpm_inst.DEF_ID_ = '1318860366413039117' ORDER BY cxId, bpm_node_jump.COMPLETE_TIME_ DESC ) tmp where 1=1
AND createTime >= #{startTime}
AND createTime<= #{endTime}
GROUP BY cxId ORDER BY finishTime DESC )total group BY monthName
