mapper.select:
select t1.id, t1.award_type awardType, t1.commend_unit commendUnit, t1.honor, t1.project_name projectName, t1.remark, t1.prizewinner, t1.portrayal_point_person_id portrayalPointPersonId, t1.portrayal_point_person_name portrayalPointPersonName, DATE_FORMAT(t1.prize_time,'%Y-%m-%d') prizeTime, t1.fileId, t1.CREATE_BY_, DATE_FORMAT(t1.CREATE_TIME_,'%Y-%m-%d') CREATE_TIME_, t2.fileName, t2.fileSize from award_manage t1 left join award_fileInfo t2 on t1.fileId = t2.id WHERE 1=1
AND t1.prizewinner like CONCAT('%',#{prizewinner},'%')
AND t1.commend_unit like CONCAT('%',#{commendUnit},'%')
AND t1.award_type = #{awardType}
AND t1.project_name like CONCAT('%',#{projectName},'%')
AND t1.prize_time >= #{rdTimeStart}
AND t1.prize_time <= #{rdTimeEnd}
order by t1.CREATE_TIME_ desc

SELECT
count( * )
FROM
award_manage t1


        AND t1.prizewinner like CONCAT('%',#{prizewinner},'%')


        AND t1.commend_unit like CONCAT('%',#{commendUnit},'%')


        AND t1.award_type = #{awardType}


        AND t1.project_name like CONCAT('%',#{projectName},'%')



        AND t1.prize_time >= #{rdTimeStart}


        AND t1.prize_time <= #{rdTimeEnd}

mapper.update:
update award_manage set award_type =#{awardType,jdbcType=VARCHAR},
project_name =#{projectName,jdbcType=VARCHAR},
honor=#{honor,jdbcType=TIMESTAMP}, commend_unit=#{commendUnit,jdbcType=VARCHAR}, remark=#{remark,jdbcType=VARCHAR}, prizewinner=#{prizewinner,jdbcType=VARCHAR}, portrayal_point_person_id =#{portrayalPointPersonId,jdbcType=VARCHAR}, portrayal_point_person_name=#{portrayalPointPersonName,jdbcType=VARCHAR}, prize_time=#{prizeTime,jdbcType=TIMESTAMP}, fileId=#{fjId,jdbcType=VARCHAR}, UPDATE_BY_=#{UPDATE_BY_,jdbcType=VARCHAR}, UPDATE_TIME_=#{UPDATE_TIME_,jdbcType=TIMESTAMP} where id=#{id}
