mapper.select:
SELECT
    A.*
FROM
    ndkfjh_plandetail A
    where id=#{detailId}
    and yearMonth = #{yearMonth}

SELECT export_info.* FROM export_info LEFT JOIN bpm_inst ON export_info.ckddId = bpm_inst.BUS_KEY_ WHERE bpm_inst.STATUS_ != 'DRAFTED'
and export_info.model = #{model}
and export_info.cpzg like CONCAT('%',#{cpzg},'%')

SELECT A.projectId, A.projectName, B.stageName as currentStage, A.currentStageId, C.planEndTime as stageFinishDate, (select min(planStartTime) from project_plan where projectId = A.projectId) as startDate, (select max(planEndTime) from project_plan where projectId = A.projectId) as endDate FROM project_baseinfo A LEFT JOIN project_stagedivide B ON A.currentStageId = B.stageId left join project_plan C on A.currentStageId = C.stageId and C.projectId = A.projectId LEFT JOIN bpm_inst D ON A.projectId = D.BUS_KEY_ where D.STATUS_ != 'DRAFTED' AND A.mainDepId in
#{deptId}
and A.projectName like CONCAT('%',#{projectName},'%')

mapper.update:
update ndkfjh_processhistory
currentStage=#{currentStage,jdbcType=VARCHAR},
stageFinishDate=#{stageFinishDate,jdbcType=TIMESTAMP},
UPDATE_BY_=#{UPDATE_BY_,jdbcType=VARCHAR},
finishRate=#{finishRate,jdbcType=VARCHAR}, isDelay=#{isDelay,jdbcType=VARCHAR}, delayDays=#{delayDays,jdbcType=VARCHAR}, remark=#{remark,jdbcType=VARCHAR}, UPDATE_TIME_=#{UPDATE_TIME_,jdbcType=TIMESTAMP}
WHERE id=#{id}

mapper.select:
SELECT A.responsor, B.FULLNAME_ AS responsorName, count( 1 ) AS totalNum FROM ndkfjh_plandetail A LEFT JOIN os_user B ON A.responsor = B.USER_ID_ WHERE A.isDel = '0' AND A.yearMonth = #{yearMonth}
and A.isDelay = #{isDelay}
GROUP BY A.responsor

SELECT B.planCode, CONCAT( DATE_FORMAT( A.startDate, '%Y-%m-%d' ), 'è‡³', DATE_FORMAT( A.endDate, '%Y-%m-%d' ) ) AS startEndDate, A.*, C.FULLNAME_ as chargerManName, D.FULLNAME_ as managerName, E.FULLNAME_ as responsorName, F.NAME_ as chargerDeptName FROM ndkfjh_plandetail A LEFT JOIN ndkfjh_plan B ON A.mainId = B.id left join os_user C on A.chargerMan = C.USER_ID_ left join os_user D on A.manager = D.USER_ID_ left join os_user E on A.responsor = E.USER_ID_ left join os_group F on A.chargerDept = F.GROUP_ID_ where A.isDel = '0' and A.yearMonth = #{yearMonth}
and E.FULLNAME_ = #{userName}
and A.isDelay = #{isDelay}
and F.NAME_ = #{deptName}
