mapper.select:
SELECT yfjb_editapply.*, yfjb_editapply.CREATE_TIME_ AS applyTime, os_user.FULLNAME_ AS userName, act_ru_task.NAME_ AS currentProcessTask, act_ru_task.ID_ AS taskId, bpm_inst.STATUS_ AS instStatus, bpm_inst.INST_ID_, os_rel_inst.PARTY1_ as applyUserDepId FROM yfjb_editapply LEFT JOIN os_user ON yfjb_editapply.CREATE_BY_ = os_user.USER_ID_ LEFT JOIN bpm_inst ON yfjb_editapply.id = bpm_inst.BUS_KEY_ LEFT JOIN act_ru_task ON bpm_inst.ACT_INST_ID_ = act_ru_task.PROC_INST_ID_ LEFT JOIN os_rel_inst ON yfjb_editapply.CREATE_BY_ = os_rel_inst.PARTY2_ LEFT JOIN os_dimension ON os_rel_inst.DIM1_ = os_dimension.DIM_ID_
AND os_rel_inst.REL_TYPE_KEY_ = 'GROUP-USER-BELONG' AND os_dimension.DIM_KEY_ = '_ADMIN'
AND yfjb_editapply.id like CONCAT ('%',#{applyId},'%')
AND os_user.FULLNAME_ like CONCAT ('%',#{applyUserName},'%')
AND yfjb_editapply.CREATE_TIME_ >= #{apply_startTime}
AND yfjb_editapply.CREATE_TIME_ <= #{apply_endTime}
AND bpm_inst.STATUS_=#{instStatus}
AND yfjb_editapply.TENANT_ID_=#{TENANT_ID_}
order by ${sortField} ${sortOrder}

select B.STATUS_ as status,A.USED as used,B.inst_id_,A.id from yfjb_editapply A left join bpm_inst B on A.id = B.BUS_KEY_ where 1=1
and A.TASKID_ = #{taskId_}
order by A.CREATE_TIME_ desc

SELECT A.id, A.reason, A.CREATE_TIME_, A.CREATE_BY_, B.FULLNAME_ as applyUserName, C.STATUS_, G.NAME_ as deptName, D.* FROM yfjb_editapply A left join os_user B on A.CREATE_BY_ = B.USER_ID_ left join bpm_inst C on A.id = C.BUS_KEY_ left join yfjb_baseinfo D on A.mainId = D.id left join os_group G on D.deptId = G.GROUP_ID_ where 1=1
AND A.id like CONCAT ('%',#{applyId},'%')
AND B.FULLNAME_ like CONCAT ('%',#{applyUserName},'%')
AND A.CREATE_TIME_ >= #{apply_startTime}
AND A.CREATE_TIME_ <= #{apply_endTime}
AND C.STATUS_=#{instStatus}
AND A.TENANT_ID_=#{TENANT_ID_}
