mapper.select:
SELECT * FROM SYS_MENU


		${whereSql}



	ORDER BY ${orderBySql}


	ORDER BY MENU_ID_ DESC

SELECT sm.* FROM SYS_MENU sm RIGHT JOIN OS_GROUP_MENU gm on sm.MENU_ID_=gm.MENU_ID_ WHERE  
gm.GROUP_ID_=#{groupId}

	AND gm.netType =#{netType}

ORDER BY sm.SN_ ASC

select DISTINCT sm.* from OS_GROUP_MENU m  join sys_menu sm on m.MENU_ID_=sm.MENU_ID_


		m.SYS_ID_=#{sysId}


		AND sm.NAME_ like CONCAT('%',#{menuName},'%')


		and m.GROUP_ID_ in

			#{item}



		AND m.netType=#{netType}

	and sm.IS_BTN_MENU_='NO'

order by sm.SN_

select * from SYS_MENU sm


		sm.SYS_ID_=#{sysId}


		AND sm.NAME_ like CONCAT('%',#{menuName},'%')

	and sm.IS_BTN_MENU_=#{isBtnMenu}

order by sm.SN_ asc

select m.* from sys_menu m, sys_type_sub_ref a, SYS_INST_TYPE t 
	where m.SYS_ID_=a.SUB_SYS_ID_ and a.INST_TYPE_ID_=t.type_id_ and t.type_code_=#{instType} 
	ORDER BY SN_ ASC

		select DISTINCT sm.* from OS_GROUP_MENU m left join sys_menu sm on m.MENU_ID_=sm.MENU_ID_
		where m.MENU_ID_=#{menuId} and m.GROUP_ID_ in 

            #{item}

SELECT sys_menu.* FROM sys_menu LEFT JOIN sys_inst_type_menu ON sys_inst_type_menu.MENU_ID_ = sys_menu.MENU_ID_ LEFT JOIN sys_inst_type on sys_inst_type_menu.INST_TYPE_ID_ = sys_inst_type.TYPE_ID_ LEFT JOIN sys_inst on sys_inst_type.TYPE_CODE_ = sys_inst.INST_TYPE_
sys_inst_type_menu.sys_id_ =#{sysId}
AND sys_menu.NAME_ like CONCAT('%',#{menuName},'%')
and sys_inst.INST_ID_ =#{tenantId} and sys_menu.IS_BTN_MENU_ =#{isBtnMenu}
order by sys_menu.SN_
