SELECT os_group.GROUP_ID_ AS depid, os_group.NAME_ AS depname, ROUND( sum( project_user_stagescore.stageScore ), 3 ) AS depsumscore FROM project_user_stagescore LEFT JOIN os_rel_inst ON project_user_stagescore.userId = os_rel_inst.PARTY2_ LEFT JOIN os_group ON os_rel_inst.PARTY1_ = os_group.GROUP_ID_
os_rel_inst.REL_TYPE_KEY_ = 'GROUP-USER-BELONG' AND os_rel_inst.DIM1_ = '1'
AND project_user_stagescore.CREATE_TIME_ >= #{timeFrom}
AND project_user_stagescore.CREATE_TIME_ <= #{timeTo}
AND os_group.GROUP_ID_ IN
#{item}
GROUP BY depid ORDER BY depsumscore desc

