AND project_baseinfo.CREATE_TIME_ >= #{buildTimeFrom}
AND project_baseinfo.CREATE_TIME_ <= #{buildTimeTo}
AND project_baseinfo.knotTime >= #{knotTimeFrom}
AND project_baseinfo.knotTime <= #{knotTimeTo}
AND project_baseinfo.mainDepId IN
#{item}
) tmp GROUP BY levelName, categoryName ORDER BY levelName, categoryName

