select ROUND(sum(A.stageScore),3) as stageScore, A.userId, A.CREATE_TIME_, B.FULLNAME_ as name, B.CERT_NO_, ( select G.name_ from os_rel_inst R left join os_group G on R.party1_ = G.group_id_ left join os_dimension C on G.dim_id_ = C.dim_id_ where R.REL_TYPE_KEY_ = 'GROUP-USER-BELONG' and R.PARTY2_ = A.userId and C.dim_key_ = '_ADMIN' limit 1 ) as deptName, ( select G.name_ from os_rel_inst R left join os_group G on R.party1_ = G.group_id_ left join os_dimension C on G.dim_id_ = C.dim_id_ where R.REL_TYPE_KEY_ = 'GROUP-USER-BELONG' and R.PARTY2_ = A.userId and C.dim_key_ = 'GW' limit 1 ) as workName, ( select G.name_ from os_rel_inst R left join os_group G on R.party1_ = G.group_id_ left join os_dimension C on G.dim_id_ = C.dim_id_ where R.REL_TYPE_KEY_ = 'GROUP-USER-BELONG' and R.PARTY2_ = A.userId and C.dim_key_ = 'ZJ' limit 1 ) as dutyName from project_user_stagescore A left join os_user B on A.userId = B.USER_ID_ LEFT JOIN os_rel_inst C ON A.userId = C.PARTY2_ LEFT JOIN os_group OG ON C.PARTY1_ = OG.GROUP_ID_ left join os_dimension OD on OG.dim_id_ = OD.dim_id_ left join project_baseinfo on A.projectId=project_baseinfo.projectId where OD.dim_key_ = '_ADMIN' and C.REL_TYPE_KEY_='GROUP-USER-BELONG'
and date_format(A.CREATE_TIME_,'%Y-%m-%d') >=#{startTime}
and date_format(A.CREATE_TIME_,'%Y-%m-%d') <=#{endTime}
and date_format(project_baseinfo.CREATE_TIME_,'%Y-%m-%d') >=#{projectStartTime}
and date_format(project_baseinfo.CREATE_TIME_,'%Y-%m-%d') <=#{projectEndTime}
and project_baseinfo.categoryId=#{categoryId}
and A.userId = #{userId}
and B.FULLNAME_ like CONCAT('%',#{userName},'%')
and OG.GROUP_ID_ in
#{item}
AND OG.GROUP_ID_ IN
#{item}
GROUP BY A.userId
order by ${sortField} ${sortOrder}
order by ${sortField} asc
order by deptName asc, stageScore desc
LIMIT ${startIndex}, ${pageSize}

