SELECT levelName, categoryName, count( * ) AS number FROM ( SELECT project_baseinfo.projectId, CONCAT( project_level.levelName, 'çº§' ) AS levelName, project_category.categoryName FROM project_baseinfo LEFT JOIN bpm_inst ON project_baseinfo.projectId = bpm_inst.BUS_KEY_ LEFT JOIN project_level ON project_baseinfo.levelId = project_level.levelId LEFT JOIN project_category ON project_baseinfo.categoryId = project_category.categoryId left join project_memberinfo ON project_memberinfo.projectId = project_baseinfo.projectId WHERE (bpm_inst.STATUS_ = 'SUCCESS_END' OR bpm_inst.STATUS_ = 'RUNNING')
AND project_baseinfo.buildTime >= #{buildTimeFrom}
AND project_baseinfo.buildTime <= #{buildTimeTo}
AND project_baseinfo.knotTime >= #{knotTimeFrom}
