SELECT project_baseinfo.*, project_source.sourceName, project_category.categoryName, project_level.levelName, project_stageDivide.stageName as currentStageName, bpm_inst.STATUS_ as status, bpm_inst.ACT_INST_ID_, project_member_role.roleName FROM project_baseinfo left join project_source on project_baseinfo.sourceId=project_source.sourceId left join project_category on project_baseinfo.categoryId=project_category.categoryId left join project_level on project_baseinfo.levelId=project_level.levelId left join project_stageDivide on project_baseinfo.currentStageId=project_stagedivide.stageId left join bpm_inst on project_baseinfo.projectId=bpm_inst.BUS_KEY_ left join project_memberinfo ON project_memberinfo.projectId = project_baseinfo.projectId left join project_member_role on project_memberinfo.roleId = project_member_role.roleId where bpm_inst.STATUS_ = 'RUNNING'
and project_memberinfo.userId = #{userId} and (project_memberinfo.userValid is null or project_memberinfo.userValid='' or project_memberinfo.userValid='01')
AND project_member_role.roleName=#{roleName}

