mapper.select:
SELECT jsjds_baseinfo.*, os_user.FULLNAME_ AS creator, bpm_inst.INST_ID_ as instId, bpm_inst.STATUS_ AS instStatus, bpm_inst.ACT_INST_ID_, os_group.NAME_ as creatorDeptName FROM jsjds_baseinfo LEFT JOIN os_user ON jsjds_baseinfo.CREATE_BY_ = os_user.USER_ID_ LEFT JOIN bpm_inst ON jsjds_baseinfo.jsjdsId = bpm_inst.BUS_KEY_ left join os_rel_inst on jsjds_baseinfo.CREATE_BY_=os_rel_inst.PARTY2_ LEFT JOIN os_group on os_rel_inst.PARTY1_=os_group.GROUP_ID_
os_rel_inst.REL_TYPE_KEY_='GROUP-USER-BELONG' and os_rel_inst.DIM1_='1'
AND jsjds_baseinfo.zlName LIKE CONCAT( '%', #{zlName}, '%' )
AND jsjds_baseinfo.CREATE_BY_ = #{applyUserId}
AND (jsjds_baseinfo.projectId is null or jsjds_baseinfo.projectId='') AND (jsjds_baseinfo.bmscwcTime is not null and jsjds_baseinfo.bmscwcTime!='')
AND os_group.GROUP_ID_ = #{applyUserDeptId}
AND (bpm_inst.STATUS_ = #{instStatus} OR bpm_inst.STATUS_ is NULL OR bpm_inst.STATUS_ = '')
AND bpm_inst.STATUS_ = #{instStatus}
AND jsjds_baseinfo.zllx = #{zllx}
AND jsjds_baseinfo.bmscwcTime >= #{bmscwcTimeStart}
AND jsjds_baseinfo.bmscwcTime <= #{bmscwcTimeEnd}

