mapper.update:
update jsjds_baseinfo
set bmscwcTime = now()
where jsjdsId = #{jsjdsId}

update jsjds_baseinfo
set bmscwcTime = null
where jsjdsId = #{jsjdsId}

update jsjds_baseinfo
set myfmsjId   = #{myfmsjId,jdbcType=VARCHAR},
    CREATE_BY_ = #{CREATE_BY_,jdbcType=VARCHAR}
where jsjdsId = #{jsjdsId}

UPDATE jsjds_baseinfo
SET jdsNum = #{jdsNum,jdbcType=VARCHAR}
WHERE jsjdsId = #{jsjdsId}

mapper.select:
SELECT os_group.GROUP_ID_ AS deptId, os_group.NAME_ AS deptName, count( 1 ) AS count FROM jsjds_baseinfo LEFT JOIN os_rel_inst ON os_rel_inst.PARTY2_ = jsjds_baseinfo.CREATE_BY_ LEFT JOIN os_group ON os_group.GROUP_ID_ = os_rel_inst.PARTY1_ left join bpm_inst on jsjds_baseinfo.jsjdsId=bpm_inst.BUS_KEY_ WHERE os_rel_inst.REL_TYPE_KEY_ ='GROUP-USER-BELONG' AND os_rel_inst.DIM1_='1' AND jsjds_baseinfo.bmscwcTime IS NOT NULL AND jsjds_baseinfo.bmscwcTime != '' AND (bpm_inst.STATUS_ != 'DISCARD_END' or bpm_inst.STATUS_ is null or bpm_inst.STATUS_='')
AND jsjds_baseinfo.zllx = #{zllx}
AND jsjds_baseinfo.bmscwcTime >= #{startTime}
AND jsjds_baseinfo.bmscwcTime <= #{endTime}
GROUP BY os_group.GROUP_ID_

mapper.update:
UPDATE zltz_zhongguozhuanli
SET  projectId    = #{projectId,jdbcType=VARCHAR},
     projectName  = #{projectName,jdbcType=VARCHAR},
     planId    = #{planId,jdbcType=VARCHAR},
     planName    = #{planName,jdbcType=VARCHAR}
WHERE jsjdsId = #{jsjdsId}
